import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
`;

const BubbleSVG = styled.svg`
  width: 100%;
  height: 100%;
  filter: drop-shadow(0 0 10px ${props => props.color}40);
`;

const BubbleInfo = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
  width: 90%;
  padding: 8px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TokenImage = styled.img`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  margin-bottom: 4px;
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 1.2em;
  text-shadow: 0 1px 2px rgba(0,0,0,0.3);
`;

const Price = styled.div`
  font-size: 0.9em;
  opacity: 0.9;
  margin: 2px 0;
`;

const PriceChange = styled.div`
  font-size: 0.8em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
`;

const MetricsContainer = styled.div`
  font-size: 0.7em;
  opacity: 0.8;
  margin-top: 4px;
  display: flex;
  flex-direction: column;
  gap: 2px;
`;

const formatNumber = (num) => {
  if (!num) return '0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(2)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(2)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(2)}K`;
  return `$${num.toFixed(2)}`;
};

const Bubble = ({ size = 100, color = '#3498db', data }) => {
  const getSize = () => {
    if (!data.marketCap) return 80;
    const cap = parseFloat(data.marketCap);
    if (cap > 1e9) return 120;
    if (cap > 1e8) return 100;
    if (cap > 1e7) return 90;
    return 80;
  };

  return (
    <BubbleContainer size={getSize()}>
      <BubbleSVG viewBox="0 0 100 100" color={color}>
        <defs>
          {/* Enhanced main gradient */}
          <radialGradient id={`bubbleGradient-${data.symbol}`} cx="30%" cy="30%">
            <stop offset="0%" stopColor="white" stopOpacity="0.8" />
            <stop offset="50%" stopColor={color} stopOpacity="0.5" />
            <stop offset="100%" stopColor={color} stopOpacity="0.9" />
          </radialGradient>
          
          {/* Enhanced highlight gradient */}
          <radialGradient id={`highlightGradient-${data.symbol}`} cx="30%" cy="20%">
            <stop offset="0%" stopColor="white" stopOpacity="0.8" />
            <stop offset="70%" stopColor="white" stopOpacity="0.2" />
            <stop offset="100%" stopColor="white" stopOpacity="0" />
          </radialGradient>
          
          {/* Glow effect */}
          <filter id={`glow-${data.symbol}`}>
            <feGaussianBlur stdDeviation="2" result="coloredBlur"/>
            <feMerge>
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
        </defs>

        {/* Base bubble with glow */}
        <circle 
          cx="50" 
          cy="50" 
          r="45"
          fill={`url(#bubbleGradient-${data.symbol})`}
          filter={`url(#glow-${data.symbol})`}
        />
        
        {/* Highlight overlay */}
        <circle 
          cx="50" 
          cy="50" 
          r="45"
          fill={`url(#highlightGradient-${data.symbol})`}
        />
      </BubbleSVG>
      <BubbleInfo>
        {data.imageUrl && (
          <TokenImage src={data.imageUrl} alt={data.symbol} />
        )}
        <Symbol>{data.symbol}</Symbol>
        {data.price && (
          <>
            <Price>${parseFloat(data.price).toFixed(4)}</Price>
            <PriceChange value={data.priceChange24h}>
              {data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(2)}%
            </PriceChange>
            <MetricsContainer>
              <div>MCap: {formatNumber(data.marketCap)}</div>
              <div>Vol: {formatNumber(data.volume24h)}</div>
              <div>Liq: {formatNumber(data.liquidity)}</div>
              <div>{data.dexId?.toUpperCase()}</div>
            </MetricsContainer>
          </>
        )}
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
