import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  border: 2px solid ${props => props.priceChange < 0 ? '#e74c3c' : '#39FF14'};
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0, 0, 0, 0.85);
  box-shadow: 
    0 0 20px ${props => props.priceChange < 0 ? '#e74c3c40' : '#39FF1440'},
    inset 0 0 30px rgba(57, 255, 20, 0.1);
  overflow: hidden;
  user-select: none;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  backdrop-filter: blur(5px);

  &:hover {
    transform: scale(1.05);
    box-shadow: 
      0 0 30px ${props => props.priceChange < 0 ? '#e74c3c60' : '#39FF1460'},
      inset 0 0 40px rgba(57, 255, 20, 0.2);
  }
`;

const Logo = styled.div`
  width: 32px;
  height: 32px;
  background-image: url(${props => props.imageUrl});
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 50%;
  margin-bottom: 4px;
  opacity: 0.9;
`;

const BubbleInfo = styled.div`
  text-align: center;
  width: 80%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  user-select: none;
  font-size: ${props => Math.max(props.size / 200, 0.8)}em;
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 1.4em;
  color: #39FF14;
  text-shadow: 0 0 10px #39FF14;
  letter-spacing: 1px;
`;

const Price = styled.div`
  font-size: 0.9em;
  color: white;
`;

const PriceChange = styled.div`
  font-size: 0.9em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
`;

const MetricsContainer = styled.div`
  font-size: 0.7em;
  color: #ccc;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1px;
  white-space: nowrap;
`;

const formatNumber = (num) => {
  if (!num) return '$0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(2)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(2)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(1)}K`;
  return `$${num.toFixed(2)}`;
};

const Bubble = ({ size, color = '#39FF14', data }) => {
  const symbol = data.baseToken?.symbol || data.symbol || 'Unknown';
  const priceChange = data.priceChange24h || 0;
  const defaultLogo = `https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/${data.chain}/assets/${data.contract}/logo.png`;
  const logoUrl = data.imageUrl || defaultLogo;

  return (
    <BubbleContainer size={size} color={color} priceChange={priceChange}>
      <BubbleInfo size={size}>
        {logoUrl && <Logo imageUrl={logoUrl} />}
        <Symbol>{symbol}</Symbol>
        {data.price && (
          <>
            <Price>${parseFloat(data.price).toFixed(4)}</Price>
            <PriceChange value={data.priceChange24h}>
              {data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(2)}%
            </PriceChange>
            <MetricsContainer>
              <div>MCap: {formatNumber(data.marketCap)}</div>
              <div>Vol: {formatNumber(data.volume24h)}</div>
            </MetricsContainer>
          </>
        )}
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
