import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: ${props => props.color}20;
  border: 2px solid ${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  
  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 20px ${props => props.color}40;
  }
`;

const BubbleInfo = styled.div`
  text-align: center;
  color: white;
  padding: 8px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 1.4em;
  margin-bottom: 4px;
`;

const Price = styled.div`
  font-size: 1em;
  opacity: 0.9;
`;

const PriceChange = styled.div`
  font-size: 0.8em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
`;

const MetricsContainer = styled.div`
  font-size: 0.8em;
  opacity: 0.8;
  margin-top: 8px;
  display: flex;
  flex-direction: column;
  gap: 4px;
  
  & > div {
    background: rgba(0,0,0,0.2);
    padding: 2px 8px;
    border-radius: 12px;
    white-space: nowrap;
  }
`;

const formatNumber = (num) => {
  if (!num) return '0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(1)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(1)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(1)}K`;
  return `$${num.toFixed(1)}`;
};

const getSize = (marketCap) => {
  if (!marketCap) return 160;  // Default size
  const cap = parseFloat(marketCap);
  if (cap > 1e9) return 200;   // Largest circles
  if (cap > 1e8) return 180;   // Large circles
  if (cap > 1e7) return 160;   // Medium circles
  return 140;                   // Small circles
};

const Bubble = ({ color = '#3498db', data }) => {
  return (
    <BubbleContainer size={getSize(data.marketCap)} color={color}>
      <BubbleInfo>
        <Symbol>{data.symbol}</Symbol>
        {data.price && (
          <>
            <Price>${parseFloat(data.price).toFixed(4)}</Price>
            <PriceChange value={data.priceChange24h}>
              {data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(2)}%
            </PriceChange>
            <MetricsContainer>
              <div>MCap: {formatNumber(data.marketCap)}</div>
              <div>Vol: {formatNumber(data.volume24h)}</div>
              <div>Liq: {formatNumber(data.liquidity)}</div>
              <div>{data.dexId?.toUpperCase()}</div>
            </MetricsContainer>
          </>
        )}
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
