import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Papa from 'papaparse';
import { fetchTokenData } from '../services/dexscreener';

const ListSelector = styled.div`
  margin-bottom: 20px;
`;

const Button = styled.button`
  background: ${props => props.active ? '#3498db' : '#2c3e50'};
  color: white;
  border: none;
  padding: 10px 20px;
  margin: 0 10px;
  border-radius: 5px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #3498db;
  }
`;

// Update the lists to include file paths
const lists = [
  { id: 'Xen', file: 'Xen.csv' },
  { id: 'Xen-Alts', file: 'Xen-Alts.csv' },
  { id: 'DBXen', file: 'DBXen.csv' }
];

const BubbleListSelector = ({ onListChange }) => {
  const [activeList, setActiveList] = useState('Xen');
  const [listData, setListData] = useState([]);

  useEffect(() => {
    loadListData(activeList);
  }, [activeList]);

  const loadListData = async (listId) => {
    try {
      // Find the correct file for the selected list
      const selectedList = lists.find(list => list.id === listId);
      if (!selectedList) {
        console.error('List not found:', listId);
        return;
      }

      const response = await fetch(`/lists/${selectedList.file}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const text = await response.text();
      
      Papa.parse(text, {
        header: true,
        complete: async (results) => {
          if (results.errors.length > 0) {
            console.error('CSV parsing errors:', results.errors);
          }

          // Filter out any empty rows
          const validData = results.data.filter(row => row.contract && row.symbol);
          
          // Fetch market data for each token
          const tokensWithData = await Promise.all(
            validData.map(async (token) => {
              try {
                const marketData = await fetchTokenData(token.contract);
                const scale = calculateScale(marketData?.marketCap);
                return {
                  ...token,
                  ...marketData,
                  scale,
                  color: marketData?.color || '#3498db'
                };
              } catch (error) {
                console.error(`Error fetching data for ${token.symbol}:`, error);
                return {
                  ...token,
                  scale: 1,
                  color: '#3498db'
                };
              }
            })
          );
          
          console.log('Loaded tokens:', tokensWithData); // Debug log
          setListData(tokensWithData);
          onListChange(tokensWithData);
        },
        error: (error) => {
          console.error('CSV parsing error:', error);
        }
      });
    } catch (error) {
      console.error('Error loading CSV:', error);
    }
  };

  const calculateScale = (marketCap) => {
    if (!marketCap) return 1;
    const cap = parseFloat(marketCap);
    if (cap > 1e9) return 1.5;
    if (cap > 1e8) return 1.3;
    if (cap > 1e7) return 1.1;
    return 1;
  };

  return (
    <ListSelector>
      {lists.map(list => (
        <Button
          key={list.id}
          active={activeList === list.id}
          onClick={() => setActiveList(list.id)}
        >
          {list.id}
        </Button>
      ))}
    </ListSelector>
  );
};

export default BubbleListSelector;
