import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  border: 2px solid #39FF14;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0, 0, 0, 0.85);
  box-shadow: 
    0 0 30px ${props => props.color === '#39FF14' ? '#39FF14' : `${props.color}40`},
    inset 0 0 20px rgba(57, 255, 20, 0.1);
  overflow: hidden;
  user-select: none;
  backdrop-filter: blur(5px);
`;

const Logo = styled.div`
  width: 48px;
  height: 48px;
  background-image: url(${props => props.imageUrl});
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 50%;
  margin-bottom: 8px;
  opacity: 0.9;
`;

const BubbleInfo = styled.div`
  text-align: center;
  width: 90%;
  height: 90%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 4px;
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 2em;
  color: #39FF14;
  text-shadow: 0 0 10px #39FF14;
  margin-bottom: 8px;
`;

const Price = styled.div`
  font-size: 1.6em;
  color: white;
  margin-bottom: 4px;
`;

const PriceChange = styled.div`
  font-size: 1.4em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
  margin-bottom: 8px;
`;

const MetricsContainer = styled.div`
  font-size: 1.2em;
  color: #ccc;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 6px;
  width: 100%;
  
  div {
    width: 90%;
    text-align: center;
    padding: 4px;
    background: rgba(0, 0, 0, 0.5);
    border-radius: 4px;
    border: 1px solid rgba(57, 255, 20, 0.2);
  }
`;

const formatNumber = (num) => {
  if (!num) return '$0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(2)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(2)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(1)}K`;
  return `$${num.toFixed(2)}`;
};

const Bubble = ({ size, color = '#39FF14', data }) => {
  const symbol = data.baseToken?.symbol || data.symbol || 'Unknown';
  const price = data.price || '0.0000';
  const priceChange = data.priceChange24h || 0;
  const marketCap = data.marketCap || 0;
  const volume = data.volume24h || 0;
  
  const getLogoUrl = (chain, contract) => {
    switch(chain.toLowerCase()) {
      case 'ethereum':
        return `https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/ethereum/assets/${contract}/logo.png`;
      case 'bsc':
        return `https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/binance/assets/${contract}/logo.png`;
      default:
        return data.imageUrl || null;
    }
  };

  const logoUrl = getLogoUrl(data.chain, data.contract);

  return (
    <BubbleContainer size={size} color={color}>
      <BubbleInfo>
        {logoUrl && <Logo imageUrl={logoUrl} />}
        <Symbol>{symbol}</Symbol>
        <Price>${parseFloat(price).toFixed(4)}</Price>
        <PriceChange value={priceChange}>
          {priceChange > 0 ? '+' : ''}{priceChange.toFixed(2)}%
        </PriceChange>
        <MetricsContainer>
          <div>MCap: {formatNumber(marketCap)}</div>
          <div>Vol: {formatNumber(volume)}</div>
        </MetricsContainer>
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
