import React from 'react';
import styled, { keyframes } from 'styled-components';
import xenLogo from '../assets/xen-logo.png';

const pulse = keyframes`
  0% {
    box-shadow: 0 0 10px rgba(57, 255, 20, 0.3),
                inset 0 0 15px rgba(57, 255, 20, 0.2);
    transform: scale(1);
  }
  50% {
    box-shadow: 0 0 20px rgba(57, 255, 20, 0.4),
                inset 0 0 25px rgba(57, 255, 20, 0.3);
    transform: scale(1.02);
  }
  100% {
    box-shadow: 0 0 10px rgba(57, 255, 20, 0.3),
                inset 0 0 15px rgba(57, 255, 20, 0.2);
    transform: scale(1);
  }
`;

const BubbleContainer = styled.div`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding-top: 15px;
  background: rgba(0, 0, 0, 0.7);
  border: 2px solid #39FF14;
  box-sizing: border-box;
  cursor: pointer;
  transition: all 0.3s ease;
  animation: ${pulse} 3s ease-in-out infinite;
  backdrop-filter: blur(5px);
  
  /* Gradient overlay */
  background: radial-gradient(
    circle at 30% 30%,
    rgba(57, 255, 20, 0.15) 0%,
    rgba(0, 0, 0, 0.9) 70%
  );
  
  /* Hover effects */
  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 30px rgba(57, 255, 20, 0.5),
                inset 0 0 30px rgba(57, 255, 20, 0.3);
    border-color: #50FF2A;
    z-index: 10;
  }
`;

const TokenSymbol = styled.div`
  color: #39FF14;
  font-size: ${props => props.size * 0.18}px;
  font-weight: bold;
  text-shadow: 0 0 10px rgba(57, 255, 20, 0.8);
  margin-bottom: 8px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 90%;
  text-align: center;
  position: relative;
  z-index: 2;
  letter-spacing: 1px;
`;

const TokenPrice = styled.div`
  color: #39FF14;
  font-size: ${props => props.size * 0.11}px;
  opacity: 0.9;
  text-shadow: 0 0 8px rgba(57, 255, 20, 0.6);
  margin: 2px 0;
`;

const PriceChange = styled.div`
  color: ${props => props.value >= 0 ? '#39FF14' : '#FF3939'};
  font-size: ${props => props.size * 0.1}px;
  font-weight: bold;
  text-shadow: 0 0 8px ${props => props.value >= 0 ? 'rgba(57, 255, 20, 0.6)' : 'rgba(255, 57, 57, 0.6)'};
`;

const Logo = styled.img`
  width: ${props => props.size * 0.35}px;
  height: ${props => props.size * 0.35}px;
  object-fit: contain;
  filter: drop-shadow(0 0 5px rgba(57, 255, 20, 0.5));
  transition: all 0.3s ease;
  margin-top: auto;
  margin-bottom: 10px;
  
  ${BubbleContainer}:hover & {
    filter: drop-shadow(0 0 8px rgba(57, 255, 20, 0.8));
    transform: scale(1.1);
  }
`;

const MarketInfo = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2px;
  margin-top: 2px;
  font-size: ${props => props.size * 0.07}px;
  color: rgba(57, 255, 20, 0.8);
  text-shadow: 0 0 5px rgba(57, 255, 20, 0.4);
`;

const InfoLabel = styled.span`
  font-size: ${props => props.size * 0.06}px;
  opacity: 0.7;
`;

const Bubble = ({ size, color = '#39FF14', data }) => {
  const symbol = data.baseToken?.symbol || data.symbol || 'Unknown';
  const priceChange = data.priceChange24h || 0;
  const defaultLogo = `https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/${data.chain}/assets/${data.contract}/logo.png`;
  const [logoSrc, setLogoSrc] = React.useState(data.imageUrl || defaultLogo);

  const formatNumber = (num) => {
    if (!num) return '---';
    
    if (num < 0.000001) {
      const str = num.toFixed(20);
      const zeroCount = str.slice(2).indexOf(/[^0]/);
      const significantDigits = str.slice(2 + zeroCount, 2 + zeroCount + 4);
      return `0.0(${zeroCount})${significantDigits}`;
    }
    
    if (num >= 1e9) return `$${(num / 1e9).toFixed(2)}B`;
    if (num >= 1e6) return `$${(num / 1e6).toFixed(2)}M`;
    if (num >= 1e3) return `$${(num / 1e3).toFixed(2)}K`;
    
    const str = num.toString();
    if (str.includes('e')) {
      const [base, exp] = str.split('e');
      const zeroCount = Math.abs(parseInt(exp)) - 1;
      return `0.0(${zeroCount})${parseFloat(base).toFixed(4)}`;
    }
    
    if (num < 1) {
      const zeroCount = num.toString().slice(2).indexOf(/[^0]/);
      if (zeroCount > 2) {
        return `0.0(${zeroCount})${num.toFixed(zeroCount + 4).slice(-4)}`;
      }
    }
    
    return `$${num.toFixed(4)}`;
  };

  const handleImageError = () => {
    setLogoSrc(xenLogo);
  };

  return (
    <BubbleContainer size={size} color={color} priceChange={priceChange}>
      <TokenSymbol size={size}>{symbol}</TokenSymbol>
      {data.price && (
        <>
          <TokenPrice size={size}>${parseFloat(data.price).toFixed(4)}</TokenPrice>
          <PriceChange size={size} value={data.priceChange24h}>
            {data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(2)}%
          </PriceChange>
          <MarketInfo size={size}>
            <div>
              <InfoLabel size={size}>MCap: </InfoLabel>
              {formatNumber(data.marketCap)}
            </div>
            <div>
              <InfoLabel size={size}>Vol: </InfoLabel>
              {formatNumber(data.volume24h)}
            </div>
          </MarketInfo>
        </>
      )}
      <Logo 
        size={size} 
        src={logoSrc} 
        alt={symbol}
        onError={handleImageError}
      />
    </BubbleContainer>
  );
};

export default Bubble;
