import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  border: 2px solid ${props => `${props.color}80`};
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  backdrop-filter: blur(5px);
  user-select: none;
  background: rgba(0, 0, 0, 0.7);
  box-shadow: 
    inset -4px -4px 8px rgba(0,0,0,0.5),
    inset 4px 4px 8px ${props => props.color}15,
    0 0 20px ${props => props.color}40;
`;

const BubbleInfo = styled.div`
  text-align: center;
  color: ${props => props.color || '#39FF14'};
  width: 90%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
`;

const Logo = styled.div`
  width: 40px;
  height: 40px;
  background-image: url(${props => props.imageUrl});
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 50%;
  border: 1px solid ${props => `${props.color}40`};
  background-color: rgba(0, 0, 0, 0.2);
`;

const TokenData = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 2px;
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 1.3em;
  color: ${props => props.color};
  text-shadow: 0 0 10px ${props => props.color};
`;

const Price = styled.div`
  font-size: 0.9em;
  opacity: 0.9;
  color: white;
`;

const ZeroCount = styled.sup`
  font-size: 0.6em;
  opacity: 0.8;
  margin-left: 1px;
  margin-right: 1px;
`;

const PriceChange = styled.div`
  font-size: 0.85em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
`;

const MetricsContainer = styled.div`
  font-size: 0.75em;
  opacity: 0.8;
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2px;
`;

const formatNumber = (num) => {
  if (!num) return '0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(1)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(1)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(1)}K`;
  return `$${num.toFixed(1)}`;
};

const getBubbleSize = (marketCap) => {
  const baseSize = 160; // Increased base size
  if (!marketCap) return baseSize;
  const cap = parseFloat(marketCap);
  if (cap > 1e6) return baseSize * 1.4;
  if (cap > 5e5) return baseSize * 1.2;
  if (cap > 1e5) return baseSize * 1.1;
  return baseSize;
};

const formatPrice = (price) => {
  if (!price) return { main: '$0' };
  const num = parseFloat(price);
  
  // Convert to string to handle scientific notation
  const str = num.toString();
  
  // If it's in scientific notation (e.g., 2.445e-10)
  if (str.includes('e-')) {
    const [base, exponent] = str.split('e-');
    const zeros = parseInt(exponent) - 1;
    return {
      main: '$0.0',
      zeros: zeros,
      value: base.replace('.', '')
    };
  }
  
  // For regular small numbers
  if (num < 1) {
    const parts = str.split('.');
    if (parts[1]) {
      return { main: `$0.${parts[1]}` };
    }
  }
  
  // For regular numbers
  return { main: `$${num.toFixed(4)}` };
};

const Bubble = ({ size, color = '#3498db', data }) => {
  const fontSize = size * 0.12;
  const symbol = data.baseToken?.symbol || data.symbol || 'Unknown';
  const priceFormat = formatPrice(data.price);
  const defaultLogo = `https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/${data.chain}/assets/${data.contract}/logo.png`;
  const logoUrl = data.imageUrl || defaultLogo;

  return (
    <BubbleContainer size={size} color={color}>
      <BubbleInfo style={{ fontSize: `${fontSize}px` }}>
        {logoUrl && <Logo imageUrl={logoUrl} color={color} />}
        <TokenData>
          <Symbol color={color}>{symbol}</Symbol>
          {data.price && (
            <>
              <Price>
                {priceFormat.main}
                {priceFormat.zeros && <ZeroCount>{priceFormat.zeros}</ZeroCount>}
                {priceFormat.value}
                <PriceChange value={data.priceChange24h}>
                  ({data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(1)}%)
                </PriceChange>
              </Price>
              <MetricsContainer>
                <div>MCap: {formatNumber(data.marketCap)}</div>
                <div>Vol: {formatNumber(data.volume24h)}</div>
              </MetricsContainer>
            </>
          )}
        </TokenData>
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
