import React from 'react';
import styled from 'styled-components';

const HeaderContainer = styled.header`
  position: sticky;
  top: 0;
  background-color: rgba(13, 13, 13, 0.9);
  padding: 1rem;
  z-index: 1000;
  backdrop-filter: blur(5px);
`;

const Nav = styled.nav`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
`;

const Logo = styled.a`
  font-size: 2.5rem;
  font-weight: bold;
  color: #39FF14;
  text-shadow: 0 0 10px #39FF14;
  text-decoration: none;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  a {
    color: #39FF14;
    text-decoration: none;
    transition: all 0.3s ease;
    font-size: 1.2rem;

    &:hover {
      color: #fff;
      text-shadow: 0 0 10px #39FF14;
    }
  }
`;

const Footer = styled.footer`
  background-color: rgba(13, 13, 13, 0.9);
  padding: 3rem 0;
  text-align: center;
  position: relative;
  z-index: 2;
`;

const SocialIcons = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
  margin-bottom: 2rem;

  img {
    width: 40px;
    height: 40px;
    transition: all 0.3s ease;
    filter: invert(1) sepia(100%) saturate(10000%) hue-rotate(80deg);

    &:hover {
      transform: scale(1.2);
      filter: invert(1) sepia(100%) saturate(10000%) hue-rotate(80deg) drop-shadow(0 0 5px #39FF14);
    }
  }
`;

const Layout = ({ children }) => {
  return (
    <>
      <HeaderContainer>
        <Nav>
          <Logo href="https://hashhead.io">HashHead.io</Logo>
          <NavLinks>
            <a href="https://store.hashhead.io">Store</a>
            <a href="https://twitter.com/TreeCityWes">Twitter</a>
            <a href="https://github.com/TreeCityWes/">GitHub</a>
            <a href="/cdn-cgi/l/email-protection#3b4c5e487b4f495e5e58524f424f495a5f52555c154e48">Contact</a>
          </NavLinks>
        </Nav>
      </HeaderContainer>

      <main>{children}</main>

      <Footer>
        <div>
          <SocialIcons>
            <a href="https://youtube.com/treecitywes">
              <img src="https://cdn-icons-png.flaticon.com/512/1384/1384060.png" alt="YouTube" />
            </a>
            <a href="https://twitter.com/treecitywes">
              <img src="https://cdn-icons-png.flaticon.com/512/733/733579.png" alt="Twitter" />
            </a>
            <a href="https://t.me/treecitytrading">
              <img src="https://cdn-icons-png.flaticon.com/512/2111/2111646.png" alt="Telegram" />
            </a>
            <a href="https://github.com/treecitywes">
              <img src="https://cdn-icons-png.flaticon.com/512/733/733553.png" alt="GitHub" />
            </a>
          </SocialIcons>
          <p>Â© 2024 HashHead.io. All rights reserved.</p>
        </div>
      </Footer>
    </>
  );
};

export default Layout;
