import React, { useState } from 'react';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';

const Container = styled.div`
  padding: 20px;
  background: #1a1a1a;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BubblesGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  justify-content: center;
  max-width: 1200px;
  padding: 20px;
  position: relative;
`;

const BubbleWrapper = styled.div`
  transition: transform 0.3s ease;
  
  &:hover {
    transform: scale(1.05);
    z-index: 10;
  }
`;

const BubbleText = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-weight: bold;
  text-shadow: 0 1px 2px rgba(0,0,0,0.3);
`;

const TooltipContent = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.9);
  border-radius: 8px;
  padding: 12px;
  font-size: 0.8em;
  color: white;
  z-index: 1000;
  pointer-events: none;
  display: none;
  
  ${BubbleWrapper}:hover & {
    display: block;
  }
`;

const SocialLink = styled.a`
  color: #3498db;
  margin: 0 4px;
  text-decoration: none;
  
  &:hover {
    text-decoration: underline;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

const BubbleDemo = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);

  const handleListChange = (newList) => {
    try {
      setError(null);
      setCurrentList(newList);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
      console.error('Error in handleListChange:', err);
    }
  };

  return (
    <Container>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        {currentList.map((token, index) => (
          <BubbleWrapper 
            key={token.symbol}
            className={index % 2 === 0 ? "bubble-float" : "bubble-pulse"}
            scale={token.scale}
          >
            <Bubble size={80} color={token.color} data={token} />
            <TooltipContent>
              <div><strong>{token.displayName}</strong></div>
              {token.websites?.map((site, i) => (
                <SocialLink key={i} href={site.url} target="_blank">Website</SocialLink>
              ))}
              {token.socials?.map((social, i) => (
                <SocialLink key={i} href={`https://${social.platform}.com/${social.handle}`} target="_blank">
                  {social.platform}
                </SocialLink>
              ))}
            </TooltipContent>
          </BubbleWrapper>
        ))}
      </BubblesGrid>
    </Container>
  );
};

export default BubbleDemo;
