import React from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.85);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #1e2b34;
  border-radius: 20px;
  padding: 20px;
  width: 90%;
  max-width: 800px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
  color: white;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: none;
  border: none;
  color: white;
  font-size: 24px;
  cursor: pointer;
  opacity: 0.7;
  
  &:hover {
    opacity: 1;
  }
`;

const TokenInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  margin-bottom: 20px;
`;

const TokenSymbol = styled.h2`
  margin: 0;
  color: ${props => props.color};
`;

const ChartPlaceholder = styled.div`
  width: 100%;
  height: 200px;
  margin: 20px 0;
  border-radius: 10px;
  background: rgba(0,0,0,0.3);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 15px;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: rgba(0,0,0,0.4);
  }
`;

const ChartMessage = styled.div`
  font-size: 1.2em;
  color: #fff;
  text-align: center;
`;

const LinkContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const Link = styled.a`
  background: #2c3e50;
  color: white;
  padding: 8px 15px;
  border-radius: 5px;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 5px;
  
  &:hover {
    background: #34495e;
  }
`;

const TokenModal = ({ token, onClose }) => {
  if (!token) return null;

  const dexscreenerUrl = `https://dexscreener.com/${token.chain}/${token.pairAddress}`;

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={e => e.stopPropagation()}>
        <CloseButton onClick={onClose}>√ó</CloseButton>
        
        <TokenInfo>
          <TokenSymbol color={token.color}>
            {token.symbol} ({token.chain})
          </TokenSymbol>
        </TokenInfo>

        <ChartPlaceholder onClick={() => window.open(dexscreenerUrl, '_blank')}>
          <ChartMessage>
            Click to view chart on DexScreener
          </ChartMessage>
          <Link as="span">üîç Open Chart</Link>
        </ChartPlaceholder>

        <LinkContainer>
          <Link href={dexscreenerUrl} target="_blank" rel="noopener noreferrer">
            üîç View on DexScreener
          </Link>
          {token.websites?.map((site, index) => (
            <Link key={index} href={site.url} target="_blank" rel="noopener noreferrer">
              üåê Website
            </Link>
          ))}
          {token.socials?.map((social, index) => (
            <Link 
              key={index}
              href={`https://${social.platform}.com/${social.handle}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {social.platform === 'twitter' ? 'üê¶' : 'üåê'} {social.platform}
            </Link>
          ))}
        </LinkContainer>
      </ModalContent>
    </ModalOverlay>
  );
};

export default TokenModal;
