import React, { useState, useEffect } from 'react';
import { useSpring, animated, config } from '@react-spring/web';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';

const Container = styled.div`
  padding: 20px;
  background: #1a1a1a;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BubblesGrid = styled.div`
  position: relative;
  width: 100vw;
  height: calc(100vh - 100px);
  overflow: hidden;
`;

const AnimatedBubbleWrapper = styled(animated.div)`
  position: absolute;
  will-change: transform;
  cursor: pointer;
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);

  const handleListChange = (newList) => {
    try {
      setError(null);
      setCurrentList(newList);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const getBubbleSize = (priceChange) => {
    const baseSize = 100;
    if (!priceChange) return baseSize;
    const change = Math.abs(priceChange);
    if (change > 20) return baseSize * 1.5;
    if (change > 10) return baseSize * 1.3;
    if (change > 5) return baseSize * 1.1;
    return baseSize;
  };

  // Create animations for all bubbles at once
  const springs = currentList.map((token, index) => {
    const size = getBubbleSize(token.priceChange24h);
    return useSpring({
      from: {
        x: Math.random() * (window.innerWidth - size),
        y: Math.random() * (window.innerHeight - size),
        rotation: Math.random() * 360,
        scale: 1,
      },
      to: async (next) => {
        while (true) {
          await next({
            x: Math.random() * (window.innerWidth - size),
            y: Math.random() * (window.innerHeight - size),
            rotation: Math.random() * 360,
            scale: 1,
            config: { tension: 100, friction: 30 }
          });
        }
      },
      config: config.gentle
    });
  });

  return (
    <Container>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        {currentList.map((token, index) => {
          const size = getBubbleSize(token.priceChange24h);
          const spring = springs[index];

          return (
            <AnimatedBubbleWrapper
              key={token.symbol}
              style={{
                transform: spring.x.to(
                  (x) => `translate3d(${x}px, ${spring.y.get()}px, 0) rotate(${spring.rotation.get()}deg) scale(${spring.scale.get()})`
                ),
                width: size,
                height: size
              }}
            >
              <Bubble
                size={size}
                color={token.color}
                data={token}
              />
            </AnimatedBubbleWrapper>
          );
        })}
      </BubblesGrid>
    </Container>
  );
};

export default BubbleContainer;
