import React, { useState, useEffect } from 'react';
import { useSpring, animated } from '@react-spring/web';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';

const Container = styled.div`
  padding: 20px;
  background: #1a1a1a;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BubblesGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
  align-items: center;
  max-width: 800px;
  padding: 20px;
  position: relative;
`;

const AnimatedBubbleWrapper = styled(animated.div)`
  position: absolute;
  transition: transform 0.3s ease;
  will-change: transform;
  
  &:hover {
    z-index: 10;
  }
`;

const BubbleText = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-weight: bold;
  text-shadow: 0 1px 2px rgba(0,0,0,0.3);
`;

const TooltipContent = styled.div`
  position: absolute;
  background: rgba(0, 0, 0, 0.9);
  border-radius: 8px;
  padding: 12px;
  font-size: 0.8em;
  color: white;
  z-index: 1000;
  pointer-events: none;
  display: none;
  
  ${AnimatedBubbleWrapper}:hover & {
    display: block;
  }
`;

const SocialLink = styled.a`
  color: #3498db;
  margin: 0 4px;
  text-decoration: none;
  
  &:hover {
    text-decoration: underline;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);
  const [positions, setPositions] = useState([]);

  // Initialize random positions for bubbles
  useEffect(() => {
    if (currentList.length) {
      const newPositions = currentList.map(() => ({
        x: Math.random() * (window.innerWidth - 200),
        y: Math.random() * (window.innerHeight - 200),
        vx: (Math.random() - 0.5) * 2,
        vy: (Math.random() - 0.5) * 2
      }));
      setPositions(newPositions);
    }
  }, [currentList]);

  // Animation loop
  useEffect(() => {
    if (!positions.length) return;

    const animate = () => {
      setPositions(prevPositions => {
        return prevPositions.map((pos, i) => {
          let { x, y, vx, vy } = pos;
          
          // Update position
          x += vx;
          y += vy;

          // Bounce off walls
          if (x <= 0 || x >= window.innerWidth - 200) vx *= -0.8;
          if (y <= 0 || y >= window.innerHeight - 200) vy *= -0.8;

          // Apply friction
          vx *= 0.99;
          vy *= 0.99;

          // Add small random movement
          vx += (Math.random() - 0.5) * 0.5;
          vy += (Math.random() - 0.5) * 0.5;

          return { x, y, vx, vy };
        });
      });
    };

    const intervalId = setInterval(animate, 50);
    return () => clearInterval(intervalId);
  }, [positions.length]);

  const handleListChange = (newList) => {
    try {
      setError(null);
      setCurrentList(newList);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const getBubbleSize = (priceChange) => {
    const baseSize = 100;
    if (!priceChange) return baseSize;
    const change = Math.abs(priceChange);
    if (change > 20) return baseSize * 1.5;
    if (change > 10) return baseSize * 1.3;
    if (change > 5) return baseSize * 1.1;
    return baseSize;
  };

  return (
    <Container>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        {currentList.map((token, index) => {
          const position = positions[index] || { x: 0, y: 0 };
          const size = getBubbleSize(token.priceChange24h);

          return (
            <AnimatedBubbleWrapper
              key={token.symbol}
              style={{
                transform: `translate(${position.x}px, ${position.y}px)`,
                width: size,
                height: size
              }}
            >
              <Bubble
                size={size}
                color={token.color}
                data={token}
              />
            </AnimatedBubbleWrapper>
          );
        })}
      </BubblesGrid>
    </Container>
  );
};

export default BubbleContainer;
