    box-shadow: 0 0 20px rgba(57, 255, 20, 0.4);
  }

  &:active {
    background: rgba(57, 255, 20, 0.3);
    box-shadow: 0 0 25px rgba(57, 255, 20, 0.5);
  }

  ${props => props.active && `
    background: rgba(57, 255, 20, 0.2);
    box-shadow: 
      0 0 20px rgba(57, 255, 20, 0.4),
      inset 0 0 10px rgba(57, 255, 20, 0.2);
  `}
`;

