import React, { useState } from 'react';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';

const Container = styled.div`
  padding: 20px;
  background: #1a1a1a;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BubblesGrid = styled.div`
  position: relative;
  width: 100vw;
  height: calc(100vh - 100px);
  overflow: hidden;
`;

const BubbleWrapper = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  animation: float ${props => 15 + Math.random() * 10}s ease-in-out infinite;
  left: ${props => props.left}%;
  top: ${props => props.top}%;
  transform-origin: center center;
  will-change: transform;
  cursor: pointer;
  
  &:hover {
    z-index: 10;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);

  const handleListChange = (newList) => {
    try {
      setError(null);
      setCurrentList(newList);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const getBubbleSize = (priceChange) => {
    const baseSize = 100;
    if (!priceChange) return baseSize;
    const change = Math.abs(priceChange);
    if (change > 20) return baseSize * 1.5;
    if (change > 10) return baseSize * 1.3;
    if (change > 5) return baseSize * 1.1;
    return baseSize;
  };

  return (
    <Container>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        {currentList.map((token) => {
          const size = getBubbleSize(token.priceChange24h);
          return (
            <BubbleWrapper
              key={token.symbol}
              size={size}
              left={Math.random() * 80}
              top={Math.random() * 80}
              className={`bubble-float-${Math.floor(Math.random() * 4)}`}
            >
              <Bubble
                size={size}
                color={token.color}
                data={token}
              />
            </BubbleWrapper>
          );
        })}
      </BubblesGrid>
    </Container>
  );
};

export default BubbleContainer;
