import React, { useState, useEffect, useRef } from 'react';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';
import TokenModal from './TokenModal';

const Container = styled.div`
  padding: 10px;
  background: transparent;
  height: calc(100vh - 140px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;

const BubblesGrid = styled.div`
  position: relative;
  width: 95vw;
  height: calc(100vh - 180px);
  margin: 0 auto;
  background: rgba(0, 0, 0, 0.8);
  border: 2px solid #39FF14;
  box-shadow: 
    0 0 30px rgba(57, 255, 20, 0.2),
    inset 0 0 30px rgba(57, 255, 20, 0.1);
  overflow: hidden;
  border-radius: 12px;
  padding: 40px 20px 20px;
  
  /* Terminal/Browser Header */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 35px;
    background: rgba(57, 255, 20, 0.1);
    border-bottom: 2px solid #39FF14;
    display: flex;
    align-items: center;
    padding: 0 10px;
  }

  /* Window Controls */
  &::after {
    content: '● ● ●';
    position: absolute;
    top: 8px;
    left: 10px;
    color: #39FF14;
    font-size: 12px;
    letter-spacing: 2px;
    text-shadow: 0 0 5px #39FF14;
  }
`;

const BubbleWrapper = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  transform-origin: center center;
  will-change: transform;
  cursor: pointer;
  
  &:hover {
    z-index: 10;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

// Add a terminal title
const TerminalTitle = styled.div`
  position: absolute;
  top: 5px;
  left: 50%;
  transform: translateX(-50%);
  color: #39FF14;
  font-size: 14px;
  font-family: monospace;
  z-index: 1;
  text-shadow: 0 0 5px #39FF14;
  &::before {
    content: '> ';
  }
  &::after {
    content: '_';
    animation: blink 1s infinite;
  }
`;

// Update the BubbleListSelector styling
const SelectorButton = styled.button`
  background: transparent;
  border: 1px solid #39FF14;
  color: #39FF14;
  padding: 8px 16px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-shadow: 0 0 5px #39FF14;
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.2);
  
  &:hover {
    background: rgba(57, 255, 20, 0.1);
    box-shadow: 0 0 15px rgba(57, 255, 20, 0.3);
  }
  
  &.active {
    background: rgba(57, 255, 20, 0.2);
    box-shadow: 0 0 20px rgba(57, 255, 20, 0.4);
  }
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);
  const [bubblePositions, setBubblePositions] = useState([]);
  const animationFrameRef = useRef();
  const containerRef = useRef();
  const dragRef = useRef(null);
  const mouseRef = useRef({ x: 0, y: 0 });
  const [selectedToken, setSelectedToken] = useState(null);

  const handleListChange = (newList) => {
    try {
      setError(null);
      const validTokens = newList.filter(token => {
        const symbol = token.baseToken?.symbol || token.symbol;
        return symbol && symbol !== 'Unknown';
      });
      setCurrentList(validTokens);
      initializeBubblePositions(validTokens);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const initializeBubblePositions = (list) => {
    const containerWidth = window.innerWidth * 0.95;
    const containerHeight = window.innerHeight - 180;
    
    const positions = list.map(token => ({
      x: Math.random() * (containerWidth - 200),
      y: Math.random() * (containerHeight - 200),
      vx: (Math.random() - 0.5) * 0.3,
      vy: (Math.random() - 0.5) * 0.3,
      size: getBubbleSize(token)
    }));
    setBubblePositions(positions);
  };

  const getBubbleSize = (token) => {
    const baseSize = 160;
    const priceChange = token.priceChange24h || 0;
    
    const multiplier = Math.min(Math.max(Math.abs(priceChange) / 10, 0.8), 2);
    
    return baseSize * multiplier;
  };

  const handleMouseDown = (index, e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    dragRef.current = {
      index,
      offsetX: e.clientX - rect.left,
      offsetY: e.clientY - rect.top
    };
    mouseRef.current = { x: e.clientX, y: e.clientY };
    
    setBubblePositions(prev => {
      const newPositions = [...prev];
      newPositions[index].isDragging = true;
      return newPositions;
    });
  };

  const handleMouseMove = (e) => {
    if (dragRef.current !== null) {
      const { index, offsetX, offsetY } = dragRef.current;
      const dx = e.clientX - mouseRef.current.x;
      const dy = e.clientY - mouseRef.current.y;
      
      setBubblePositions(prev => {
        const newPositions = [...prev];
        newPositions[index].x += dx;
        newPositions[index].y += dy;
        newPositions[index].vx = dx * 0.1;
        newPositions[index].vy = dy * 0.1;
        return newPositions;
      });
      
      mouseRef.current = { x: e.clientX, y: e.clientY };
    }
  };

  const handleMouseUp = () => {
    if (dragRef.current !== null) {
      const index = dragRef.current.index;
      setBubblePositions(prev => {
        const newPositions = [...prev];
        if (newPositions[index]) {
          newPositions[index].isDragging = false;
        }
        return newPositions;
      });
      dragRef.current = null;
    }
  };

  const updateBubblePositions = () => {
    setBubblePositions(prevPositions => {
      const containerWidth = window.innerWidth * 0.95;
      const containerHeight = window.innerHeight - 250;
      const newPositions = [...prevPositions];
      
      // Update positions based on velocity
      for (let i = 0; i < newPositions.length; i++) {
        if (newPositions[i].isDragging) continue;
        
        newPositions[i].x += newPositions[i].vx;
        newPositions[i].y += newPositions[i].vy;
        
        // Gentle bounce off walls
        const size = newPositions[i].size;
        if (newPositions[i].x <= 0 || newPositions[i].x >= containerWidth - size) {
          newPositions[i].vx *= -0.8;
          newPositions[i].x = Math.max(0, Math.min(newPositions[i].x, containerWidth - size));
        }
        if (newPositions[i].y <= 0 || newPositions[i].y >= containerHeight - size) {
          newPositions[i].vy *= -0.8;
          newPositions[i].y = Math.max(0, Math.min(newPositions[i].y, containerHeight - size));
        }

        // Apply gentle friction
        newPositions[i].vx *= 0.99;
        newPositions[i].vy *= 0.99;
        
        // Add very subtle random movement
        if (Math.abs(newPositions[i].vx) < 0.1 && Math.abs(newPositions[i].vy) < 0.1) {
          newPositions[i].vx += (Math.random() - 0.5) * 0.05;
          newPositions[i].vy += (Math.random() - 0.5) * 0.05;
        }
      }

      // Softer collisions between bubbles
      for (let i = 0; i < newPositions.length; i++) {
        if (newPositions[i].isDragging) continue;
        for (let j = i + 1; j < newPositions.length; j++) {
          if (newPositions[j].isDragging) continue;
          
          const dx = newPositions[i].x - newPositions[j].x;
          const dy = newPositions[i].y - newPositions[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const minDistance = (newPositions[i].size + newPositions[j].size) / 1.5;

          if (distance < minDistance) {
            const angle = Math.atan2(dy, dx);
            
            // Gentler collision response
            const overlap = (minDistance - distance) * 0.7;
            newPositions[i].x += overlap * Math.cos(angle);
            newPositions[i].y += overlap * Math.sin(angle);
            newPositions[j].x -= overlap * Math.cos(angle);
            newPositions[j].y -= overlap * Math.sin(angle);

            // Exchange velocities more naturally
            const tempVx = newPositions[i].vx;
            const tempVy = newPositions[i].vy;
            newPositions[i].vx = newPositions[j].vx * 0.95;
            newPositions[i].vy = newPositions[j].vy * 0.95;
            newPositions[j].vx = tempVx * 0.95;
            newPositions[j].vy = tempVy * 0.95;
          }
        }
      }

      return newPositions;
    });

    animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
  };

  // Start animation loop
  useEffect(() => {
    if (currentList.length > 0) {
      animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
    }
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [currentList]);

  // Add mouse event listeners
  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  // Initialize positions when component mounts
  useEffect(() => {
    if (currentList.length > 0) {
      initializeBubblePositions(currentList);
    }
  }, []);

  const handleBubbleClick = (token, e) => {
    // Don't show modal if we're dragging
    if (dragRef.current) return;
    setSelectedToken(token);
  };

  return (
    <Container ref={containerRef}>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        <TerminalTitle>XEN Network Monitor v1.0</TerminalTitle>
        {currentList
          .filter(token => {
            const symbol = token.baseToken?.symbol || token.symbol;
            return symbol && symbol !== 'Unknown';
          })
          .map((token, index) => {
            const position = bubblePositions[index] || { 
              x: 0, 
              y: 0, 
              size: getBubbleSize(token) 
            };
            return (
              <BubbleWrapper
                key={token.symbol}
                size={position.size}
                style={{
                  transform: `translate(${position.x}px, ${position.y}px)`
                }}
                onMouseDown={(e) => handleMouseDown(index, e)}
                onClick={(e) => handleBubbleClick(token, e)}
              >
                <Bubble
                  size={position.size}
                  color={token.color}
                  data={token}
                />
              </BubbleWrapper>
            );
        })}
      </BubblesGrid>
      {selectedToken && (
        <TokenModal
          token={selectedToken}
          onClose={() => setSelectedToken(null)}
        />
      )}
    </Container>
  );
};

export default BubbleContainer;
