import React, { useState, useEffect, useRef } from 'react';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';
import TokenModal from './TokenModal';

const Container = styled.div`
  padding: 10px;
  background: transparent;
  height: calc(100vh - 140px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;

const BubblesGrid = styled.div`
  position: relative;
  width: 95vw;
  height: calc(100vh - 180px);
  margin: 0 auto;
  background: rgba(0, 0, 0, 0.8);
  border: 2px solid #39FF14;
  box-shadow: 
    0 0 30px rgba(57, 255, 20, 0.2),
    inset 0 0 30px rgba(57, 255, 20, 0.1);
  overflow: hidden;
  border-radius: 12px;
  padding: 40px 20px 20px;
  
  /* Terminal/Browser Header */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 35px;
    background: rgba(57, 255, 20, 0.1);
    border-bottom: 2px solid #39FF14;
    display: flex;
    align-items: center;
    padding: 0 10px;
  }

  /* Window Controls */
  &::after {
    content: '● ● ●';
    position: absolute;
    top: 8px;
    left: 10px;
    color: #39FF14;
    font-size: 12px;
    letter-spacing: 2px;
    text-shadow: 0 0 5px #39FF14;
  }
`;

const BubbleWrapper = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  transform-origin: center center;
  will-change: transform;
  cursor: pointer;
  
  &:hover {
    z-index: 10;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

// Add a terminal title
const TerminalTitle = styled.div`
  position: absolute;
  top: 5px;
  left: 50%;
  transform: translateX(-50%);
  color: #39FF14;
  font-size: 14px;
  font-family: monospace;
  z-index: 1;
  text-shadow: 0 0 5px #39FF14;
  &::before {
    content: '> ';
  }
  &::after {
    content: '_';
    animation: blink 1s infinite;
  }
`;

// Update the BubbleListSelector styling
const SelectorButton = styled.button`
  background: transparent;
  border: 1px solid #39FF14;
  color: #39FF14;
  padding: 8px 16px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-shadow: 0 0 5px #39FF14;
  box-shadow: 0 0 10px rgba(57, 255, 20, 0.2);
  
  &:hover {
    background: rgba(57, 255, 20, 0.1);
    box-shadow: 0 0 15px rgba(57, 255, 20, 0.3);
  }
  
  &.active {
    background: rgba(57, 255, 20, 0.2);
    box-shadow: 0 0 20px rgba(57, 255, 20, 0.4);
  }
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);
  const [bubblePositions, setBubblePositions] = useState([]);
  const animationFrameRef = useRef();
  const containerRef = useRef();
  const dragRef = useRef(null);
  const mouseRef = useRef({ x: 0, y: 0 });
  const [selectedToken, setSelectedToken] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const lastDragTime = useRef(0);

  const handleListChange = (newList) => {
    try {
      setError(null);
      const validTokens = newList.filter(token => {
        const symbol = token.baseToken?.symbol || token.symbol;
        return symbol && symbol !== 'Unknown';
      });
      setCurrentList(validTokens);
      initializeBubblePositions(validTokens);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const initializeBubblePositions = (list) => {
    const containerWidth = window.innerWidth * 0.95;
    const containerHeight = window.innerHeight - 180;
    const padding = 100;
    
    // Start from center and spiral outward
    let centerX = containerWidth / 2;
    let centerY = containerHeight / 2;
    let angle = 0;
    let radius = 100;
    let positions = [];
    
    list.forEach((token) => {
      const size = getBubbleSize(token);
      
      // Find position in spiral pattern
      let x, y;
      let attempts = 0;
      let placed = false;
      
      while (!placed && attempts < 100) {
        x = centerX + radius * Math.cos(angle);
        y = centerY + radius * Math.sin(angle);
        
        // Check if position is valid
        if (x >= padding && x <= containerWidth - size - padding &&
            y >= padding && y <= containerHeight - size - padding) {
          
          // Check for overlaps
          const overlaps = positions.some(pos => {
            const dx = pos.x - x;
            const dy = pos.y - y;
            const minDist = (pos.size + size) * 0.8; // Reduced overlap check
            return Math.sqrt(dx * dx + dy * dy) < minDist;
          });
          
          if (!overlaps) {
            placed = true;
          }
        }
        
        if (!placed) {
          angle += Math.PI / 4; // Increased angle step
          if (angle >= Math.PI * 2) {
            angle = 0;
            radius += size; // Increase radius by bubble size
          }
        }
        attempts++;
      }
      
      // Add some randomness to initial velocities
      positions.push({
        x: Math.max(size, Math.min(x, containerWidth - size)),
        y: Math.max(size, Math.min(y, containerHeight - size)),
        vx: (Math.random() - 0.5) * 0.5,
        vy: (Math.random() - 0.5) * 0.5,
        size
      });
    });
    
    setBubblePositions(positions);
  };

  const getBubbleSize = (token) => {
    const baseSize = 140; // Slightly reduced base size
    const priceChange = token.priceChange24h || 0;
    
    // More gradual size scaling based on price change
    const multiplier = Math.min(Math.max(Math.abs(priceChange) / 15, 0.8), 1.8);
    
    return baseSize * multiplier;
  };

  const handleMouseDown = (index, e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    dragRef.current = {
      index,
      offsetX: e.clientX - rect.left,
      offsetY: e.clientY - rect.top,
      startX: e.clientX,
      startY: e.clientY,
      timestamp: Date.now()
    };
    mouseRef.current = { x: e.clientX, y: e.clientY };
    setIsDragging(true);
    
    setBubblePositions(prev => {
      const newPositions = [...prev];
      newPositions[index].isDragging = true;
      return newPositions;
    });
  };

  const handleMouseMove = (e) => {
    if (dragRef.current !== null) {
      const { index } = dragRef.current;
      const dx = e.clientX - mouseRef.current.x;
      const dy = e.clientY - mouseRef.current.y;
      
      setBubblePositions(prev => {
        const newPositions = [...prev];
        newPositions[index].x += dx;
        newPositions[index].y += dy;
        
        // Calculate velocity based on movement
        const now = Date.now();
        const dt = now - lastDragTime.current;
        if (dt > 0) {
          newPositions[index].vx = dx / dt * 16; // Scale for 60fps
          newPositions[index].vy = dy / dt * 16;
        }
        lastDragTime.current = now;
        
        return newPositions;
      });
      
      mouseRef.current = { x: e.clientX, y: e.clientY };
    }
  };

  const handleMouseUp = () => {
    if (dragRef.current !== null) {
      const index = dragRef.current.index;
      setBubblePositions(prev => {
        const newPositions = [...prev];
        newPositions[index].isDragging = false;
        return newPositions;
      });
      
      // Add small delay before clearing drag state
      setTimeout(() => {
        setIsDragging(false);
      }, 100);
      
      dragRef.current = null;
    }
  };

  const updateBubblePositions = () => {
    setBubblePositions(prevPositions => {
      const containerWidth = window.innerWidth * 0.95;
      const containerHeight = window.innerHeight - 180;
      const newPositions = [...prevPositions];
      
      // Update positions based on velocity
      for (let i = 0; i < newPositions.length; i++) {
        if (newPositions[i].isDragging) continue;
        
        // Apply velocity
        newPositions[i].x += newPositions[i].vx;
        newPositions[i].y += newPositions[i].vy;
        
        // Bounce off walls
        const size = newPositions[i].size;
        if (newPositions[i].x <= 0 || newPositions[i].x >= containerWidth - size) {
          newPositions[i].vx *= -0.8;
          newPositions[i].x = Math.max(0, Math.min(newPositions[i].x, containerWidth - size));
        }
        if (newPositions[i].y <= 0 || newPositions[i].y >= containerHeight - size) {
          newPositions[i].vy *= -0.8;
          newPositions[i].y = Math.max(0, Math.min(newPositions[i].y, containerHeight - size));
        }

        // Apply very slight friction
        newPositions[i].vx *= 0.995;
        newPositions[i].vy *= 0.995;
      }

      // Handle collisions between bubbles
      for (let i = 0; i < newPositions.length; i++) {
        for (let j = i + 1; j < newPositions.length; j++) {
          const dx = newPositions[j].x - newPositions[i].x;
          const dy = newPositions[j].y - newPositions[i].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const minDistance = (newPositions[i].size + newPositions[j].size) * 0.6;

          if (distance < minDistance) {
            // Calculate collision response
            const angle = Math.atan2(dy, dx);
            const speed1 = Math.sqrt(newPositions[i].vx * newPositions[i].vx + 
                                   newPositions[i].vy * newPositions[i].vy);
            const speed2 = Math.sqrt(newPositions[j].vx * newPositions[j].vx + 
                                   newPositions[j].vy * newPositions[j].vy);
            
            // Move bubbles apart
            const overlap = minDistance - distance;
            const moveX = (overlap * Math.cos(angle)) / 2;
            const moveY = (overlap * Math.sin(angle)) / 2;
            
            newPositions[i].x -= moveX;
            newPositions[i].y -= moveY;
            newPositions[j].x += moveX;
            newPositions[j].y += moveY;
            
            // Exchange velocities
            const tempVx = newPositions[i].vx;
            const tempVy = newPositions[i].vy;
            newPositions[i].vx = newPositions[j].vx * 0.6;
            newPositions[i].vy = newPositions[j].vy * 0.6;
            newPositions[j].vx = tempVx * 0.6;
            newPositions[j].vy = tempVy * 0.6;
          }
        }
      }

      return newPositions;
    });

    animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
  };

  // Start animation loop
  useEffect(() => {
    if (currentList.length > 0) {
      animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
    }
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [currentList]);

  // Add mouse event listeners
  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  // Initialize positions when component mounts
  useEffect(() => {
    if (currentList.length > 0) {
      initializeBubblePositions(currentList);
    }
  }, []);

  const handleBubbleClick = (token, e) => {
    // Only show modal if we haven't been dragging
    const dragDistance = dragRef.current ? 
      Math.hypot(
        e.clientX - dragRef.current.startX,
        e.clientY - dragRef.current.startY
      ) : 0;
    
    const dragTime = dragRef.current ?
      Date.now() - dragRef.current.timestamp : 0;
    
    if (!isDragging && dragDistance < 5 && dragTime < 200) {
      setSelectedToken(token);
    }
  };

  return (
    <Container ref={containerRef}>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        <TerminalTitle>XEN Network Monitor v1.0</TerminalTitle>
        {currentList
          .filter(token => {
            const symbol = token.baseToken?.symbol || token.symbol;
            return symbol && symbol !== 'Unknown';
          })
          .map((token, index) => {
            const position = bubblePositions[index] || { 
              x: 0, 
              y: 0, 
              size: getBubbleSize(token) 
            };
            return (
              <BubbleWrapper
                key={token.symbol}
                size={position.size}
                style={{
                  transform: `translate(${position.x}px, ${position.y}px)`
                }}
                onMouseDown={(e) => handleMouseDown(index, e)}
                onClick={(e) => handleBubbleClick(token, e)}
              >
                <Bubble
                  size={position.size}
                  color={token.color}
                  data={token}
                />
              </BubbleWrapper>
            );
        })}
      </BubblesGrid>
      {selectedToken && (
        <TokenModal
          token={selectedToken}
          onClose={() => setSelectedToken(null)}
        />
      )}
    </Container>
  );
};

export default BubbleContainer;
