import React, { useState, useEffect, useRef } from 'react';
import Bubble from './Bubble';
import BubbleListSelector from './BubbleListSelector';
import styled from 'styled-components';
import '../styles/bubble-animations.css';

const Container = styled.div`
  padding: 20px;
  background: #1a1a1a;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BubblesGrid = styled.div`
  position: relative;
  width: 100vw;
  height: calc(100vh - 100px);
  overflow: hidden;
`;

const BubbleWrapper = styled.div`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  transform-origin: center center;
  will-change: transform;
  cursor: pointer;
  transition: transform 0.1s linear;
  
  &:hover {
    z-index: 10;
  }
`;

const ErrorMessage = styled.div`
  color: #e74c3c;
  padding: 20px;
  text-align: center;
  background: rgba(231, 76, 60, 0.1);
  border-radius: 8px;
  margin: 20px;
`;

const BubbleContainer = () => {
  const [currentList, setCurrentList] = useState([]);
  const [error, setError] = useState(null);
  const [bubblePositions, setBubblePositions] = useState([]);
  const animationFrameRef = useRef();
  const containerRef = useRef();

  const handleListChange = (newList) => {
    try {
      setError(null);
      setCurrentList(newList);
      // Initialize bubble positions when list changes
      initializeBubblePositions(newList);
    } catch (err) {
      setError('Error loading token list: ' + err.message);
    }
  };

  const initializeBubblePositions = (list) => {
    const positions = list.map(token => ({
      x: Math.random() * (window.innerWidth - 150),
      y: Math.random() * (window.innerHeight - 150),
      vx: (Math.random() - 0.5) * 2,
      vy: (Math.random() - 0.5) * 2,
      size: getBubbleSize(token.priceChange24h)
    }));
    setBubblePositions(positions);
  };

  const getBubbleSize = (priceChange) => {
    const baseSize = 100;
    if (!priceChange) return baseSize;
    const change = Math.abs(priceChange);
    if (change > 20) return baseSize * 1.5;
    if (change > 10) return baseSize * 1.3;
    if (change > 5) return baseSize * 1.1;
    return baseSize;
  };

  const updateBubblePositions = () => {
    setBubblePositions(prevPositions => {
      const newPositions = [...prevPositions];
      
      // Update positions based on velocity
      for (let i = 0; i < newPositions.length; i++) {
        newPositions[i].x += newPositions[i].vx;
        newPositions[i].y += newPositions[i].vy;
        
        // Bounce off walls
        const size = newPositions[i].size;
        if (newPositions[i].x <= 0 || newPositions[i].x >= window.innerWidth - size) {
          newPositions[i].vx *= -1;
        }
        if (newPositions[i].y <= 0 || newPositions[i].y >= window.innerHeight - size) {
          newPositions[i].vy *= -1;
        }

        // Apply friction
        newPositions[i].vx *= 0.99;
        newPositions[i].vy *= 0.99;

        // Add small random movement
        newPositions[i].vx += (Math.random() - 0.5) * 0.1;
        newPositions[i].vy += (Math.random() - 0.5) * 0.1;
      }

      // Check collisions between bubbles
      for (let i = 0; i < newPositions.length; i++) {
        for (let j = i + 1; j < newPositions.length; j++) {
          const dx = newPositions[i].x - newPositions[j].x;
          const dy = newPositions[i].y - newPositions[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const minDistance = (newPositions[i].size + newPositions[j].size) / 2;

          if (distance < minDistance) {
            // Collision detected - calculate new velocities
            const angle = Math.atan2(dy, dx);
            const speed1 = Math.sqrt(newPositions[i].vx * newPositions[i].vx + newPositions[i].vy * newPositions[i].vy);
            const speed2 = Math.sqrt(newPositions[j].vx * newPositions[j].vx + newPositions[j].vy * newPositions[j].vy);

            // Move bubbles apart
            const overlap = minDistance - distance;
            newPositions[i].x += overlap * Math.cos(angle) / 2;
            newPositions[i].y += overlap * Math.sin(angle) / 2;
            newPositions[j].x -= overlap * Math.cos(angle) / 2;
            newPositions[j].y -= overlap * Math.sin(angle) / 2;

            // Exchange velocities
            const tempVx = newPositions[i].vx;
            const tempVy = newPositions[i].vy;
            newPositions[i].vx = newPositions[j].vx * 0.95;
            newPositions[i].vy = newPositions[j].vy * 0.95;
            newPositions[j].vx = tempVx * 0.95;
            newPositions[j].vy = tempVy * 0.95;
          }
        }
      }

      return newPositions;
    });

    animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
  };

  useEffect(() => {
    animationFrameRef.current = requestAnimationFrame(updateBubblePositions);
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [currentList]);

  return (
    <Container ref={containerRef}>
      <BubbleListSelector onListChange={handleListChange} />
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <BubblesGrid>
        {currentList.map((token, index) => {
          const position = bubblePositions[index] || { x: 0, y: 0 };
          return (
            <BubbleWrapper
              key={token.symbol}
              size={position.size}
              style={{
                transform: `translate(${position.x}px, ${position.y}px)`
              }}
            >
              <Bubble
                size={position.size}
                color={token.color}
                data={token}
              />
            </BubbleWrapper>
          );
        })}
      </BubblesGrid>
    </Container>
  );
};

export default BubbleContainer;
