import React from 'react';
import styled from 'styled-components';

const BubbleContainer = styled.div`
  position: relative;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  border: 2px solid ${props => `${props.color}80`};
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  backdrop-filter: blur(5px);
  user-select: none;
  
  /* Refined 3D effects */
  background: radial-gradient(
    circle at 35% 35%,
    ${props => props.color}20,
    ${props => props.color}10 40%,
    ${props => props.color}05 100%
  );
  box-shadow: 
    inset -4px -4px 8px rgba(0,0,0,0.1),
    inset 4px 4px 8px ${props => props.color}15,
    0 0 20px ${props => props.color}20;
  transform-style: preserve-3d;
  perspective: 1000px;
  
  &:hover {
    transform: scale(1.05) translateZ(5px);
    box-shadow: 
      inset -4px -4px 8px rgba(0,0,0,0.2),
      inset 4px 4px 8px ${props => props.color}25,
      0 0 30px ${props => props.color}30;
    z-index: 10;
    border-color: ${props => props.color};
  }
  
  /* Subtle highlight */
  &:before {
    content: '';
    position: absolute;
    top: 10%;
    left: 10%;
    width: 20%;
    height: 20%;
    background: radial-gradient(
      circle at 50% 50%,
      rgba(255,255,255,0.15),
      transparent 80%
    );
    border-radius: 50%;
    transform: translateZ(1px);
    opacity: 0.8;
  }
  
  /* Subtle shadow */
  &:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: radial-gradient(
      circle at 50% 50%,
      transparent 60%,
      rgba(0,0,0,0.1) 100%
    );
    transform: translateZ(-1px);
  }
`;

const BubbleInfo = styled.div`
  text-align: center;
  color: white;
  width: 85%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2px;
  transform: translateZ(2px);
  text-shadow: 0 1px 2px rgba(0,0,0,0.2);
`;

const Symbol = styled.div`
  font-weight: bold;
  font-size: 1.2em;
  color: ${props => props.color};
`;

const Price = styled.div`
  font-size: 0.9em;
  opacity: 0.9;
  display: flex;
  align-items: start;
  justify-content: center;
`;

const ZeroCount = styled.sup`
  font-size: 0.6em;
  opacity: 0.8;
  margin-left: 1px;
  margin-right: 1px;
`;

const PriceChange = styled.div`
  font-size: 0.85em;
  color: ${props => props.value > 0 ? '#2ecc71' : '#e74c3c'};
  font-weight: bold;
`;

const MetricsContainer = styled.div`
  font-size: 0.75em;
  opacity: 0.8;
  margin-top: 2px;
  display: flex;
  flex-direction: column;
  gap: 2px;
  
  & > div {
    padding: 1px 4px;
    white-space: nowrap;
  }
`;

const formatNumber = (num) => {
  if (!num) return '0';
  if (num >= 1e9) return `$${(num / 1e9).toFixed(1)}B`;
  if (num >= 1e6) return `$${(num / 1e6).toFixed(1)}M`;
  if (num >= 1e3) return `$${(num / 1e3).toFixed(1)}K`;
  return `$${num.toFixed(1)}`;
};

const getSize = (marketCap) => {
  if (!marketCap) return 140;  // Default size
  const cap = parseFloat(marketCap);
  if (cap > 1e9) return 160;   // Largest circles
  if (cap > 1e8) return 150;   // Large circles
  if (cap > 1e7) return 140;   // Medium circles
  return 130;                   // Small circles
};

const formatPrice = (price) => {
  if (!price) return { main: '$0' };
  const num = parseFloat(price);
  
  // Convert to string to handle scientific notation
  const str = num.toString();
  
  // If it's in scientific notation (e.g., 2.445e-10)
  if (str.includes('e-')) {
    const [base, exponent] = str.split('e-');
    const zeros = parseInt(exponent) - 1;
    return {
      main: '$0.0',
      zeros: zeros,
      value: base.replace('.', '')
    };
  }
  
  // For regular small numbers
  if (num < 1) {
    const parts = str.split('.');
    if (parts[1]) {
      return { main: `$0.${parts[1]}` };
    }
  }
  
  // For regular numbers
  return { main: `$${num.toFixed(4)}` };
};

const Bubble = ({ size, color = '#3498db', data }) => {
  const fontSize = size * 0.12;
  const symbol = data.baseToken?.symbol || data.symbol || 'Unknown';
  const priceFormat = formatPrice(data.price);
  
  return (
    <BubbleContainer size={size} color={color}>
      <BubbleInfo style={{ fontSize: `${fontSize}px` }}>
        <Symbol color={color}>{symbol}</Symbol>
        {data.price && (
          <>
            <Price>
              {priceFormat.main}
              {priceFormat.zeros && <ZeroCount>{priceFormat.zeros}</ZeroCount>}
              {priceFormat.value}
            </Price>
            <PriceChange value={data.priceChange24h}>
              {data.priceChange24h > 0 ? '+' : ''}{data.priceChange24h?.toFixed(2)}%
            </PriceChange>
            <MetricsContainer>
              <div>MCap: {formatNumber(data.marketCap)}</div>
              <div>Vol: {formatNumber(data.volume24h)}</div>
            </MetricsContainer>
          </>
        )}
      </BubbleInfo>
    </BubbleContainer>
  );
};

export default Bubble;
